void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    const float pi = 3.1415926535;
    vec2 p = fragCoord.xy - iResolution.xy / 2.0;
    float phi = atan(p.y, p.x + 1e-5);

    float fin = mod(floor(phi * 3.0 / pi + 0.5), 6.0);
    float phi_fin = fin * pi / 3.0;

    vec2 dir = vec2(cos(phi_fin), sin(phi_fin));
    float l = dot(dir, p) - iTime * iResolution.y / 8.0;
    float seg = floor(l * 70.0 / iResolution.y);

    float th = sin(iTime) * 0.4 + 0.5;
    float t = sin(seg * 92.198763) * iTime;
    fragColor = vec4(step(th, fract(phi / pi / 2.0 + t)));
}
