///Barakooda 04/02/2017

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //noramelize 
	vec2 uv = fragCoord.xy / iResolution.xy;
   	//normarlize mouse x y
    vec2 mouse = iMouse.xy/iResolution.xy;


    //window aspect
    float aspect = iResolution.y/iResolution.x;

    //axis change from 0 - 1.0 to -1.0 - 1.0
	uv = uv * 2. - 1.;
	mouse = mouse * 2. - 1.;

    //Strecth y axis
    uv.y    *= aspect;
	mouse.y *= aspect;


    // creating buttom left rectangle point (x buttom, y buttom)
    float xb = step(mouse.x - .1,uv.x);
    float yb = step(mouse.y - .1,uv.y);

    // creating top right rectangle point (x top , y top) 
    float xt = step(-(mouse.x + .1),-uv.x);
    float yt = step(-(mouse.y + .1),-uv.y);

    // multiplie step layers
    float color = xt*yt * xb*yb;



    //final
	fragColor = vec4(vec3(color),1.);
}
