float getAverage(float start, float end) {
    float sum = 0.0;
    int count = 0;
    for(float i = start; i < end; i += 1.0/512.0) {
        sum += texture(iChannel0, vec2(i, 0.25)).x;
        count++;
    }
    float average = sum/float(count);
    return average;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

	float bass = getAverage(0.001, 0.01);
    float mids = getAverage(0.01, 0.3);  
    float highs = getAverage(0.3, 1.0);

    bool c;
    if(uv.x < 0.33) {
        c = bass > uv.y;
    } else if(uv.x > 0.33 && uv.x < 0.66) {
        c = mids > uv.y;
    } else if(uv.x > 0.66) {
    	c = highs > uv.y;
    }      

	fragColor = vec4(vec3(c), 1.0);
}
