#define PI 3.1415926535897932384626433832795
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Tid t; antal sekunder fra start, i nanosekunds præcision
    float t = iTime;

	// Dette er de individuelle pixels; dette kode er for én pixel
    vec3 pixel = texture(iChannel0, fragCoord.xy / iResolution.xy).rgb;

    // De individuelle farver i en pixel (rød, grøn, blå; grundfarver)
    // 0: 0% lys, 1: 100% lys - lys dikterer farvens betydning 
    float red = pixel.r;
    float green = pixel.g;
    float blue = pixel.b;

    // Vend alt rødt omvendt; invers rød
    red = 1.0 - red;

    // Blåt påvirkes af en sinus kurve der bevæger sig i tid t
    blue = sin(t * PI) * blue; 


    // Tegn denne pixel på skærmen
    fragColor = vec4(vec3(red, green, blue), 1.0);
}
