#define n 256.
													// next
void mainImage(out vec4 o,vec2 u)					// output
{
    vec3 R = iResolution;							// Resolution
    u.x = R.x-u.x;									// Horizontal mirror
    float f = float(int(1081./(R.y+1.)));			// factor
	float s = 64./f*iDate.w;						// speed
    float t = mod(s,n*4.);							// time
    vec2 c1 = vec2(.35*R.x,.5*R.y);					// center1
    vec2 c2 = vec2(.65*R.x,.5*R.y);					// center2
    vec2 v1 = vec2(u.x-c1.x,2.*(u.y-c1.y));			// vector1
    vec2 v2 = vec2(u.x-c2.x,2.*(u.y-c2.y));			// vector2
    float d,d1 = length(v1);						// distance1
    float d2 = length(v2);							// distance2
    float a,a1 = atan(v1.x,v1.y)-d1/R.y+s*f/512.;	// angle1
    float a2 = atan(v2.x,v2.y)-d2/R.y-s*f/512.;		// angle2
    int i,i1 = 1^int(32.*(d1+s)/R.y);				// intensity1
    int i2 = int(32.*(d2-s)/R.y);					// intensity2
    float m = c2.x-c1.x;							// max distance
    if(u.y>c1.y)
    {
    	i = i1;
    	a = a1;
    	if(d1>m && u.x>c1.x)
        {
            i = i2;
            a = a2;
        }
    } else
    {
    	i = i2;
    	a = a2;
    	if(d2>m && u.x<c2.x)
        {
            i = i1;
            a = a1;
        }
    }
    if(u.x<.5*R.x)
    {
        d = d1;
    } else
    {
        d = d2;
    }

    if(t>n*2.)
    {
        i = i1;
        a = a1;
        d = d1;
    }
    if(t>n*3.)
    {
        i = i2;
        a = a2;
        d = d2;
    }
    m = ((float(1&i)*((R.y-u.y)/R.y)*1.)+(0.05*float(1&int(a*22.5))))/(256./f)*d*2.;
//  m = ((float(1&i)*((R.y-u.y)/R.y)*1.75))/(256./f)*d;
//  m = ((0.05*float(1&int(a*22.5))))/(256./f)*d*10.;
//  m = ((0.05*float(1&int(a*22.5))))/(256./f)*d*((R.y-u.y)/R.y)*16.;
    if((1&i)==0) o = m*vec4(1.,.5,.0,1.);
    else o = m*vec4(.5,.0,.25,1.);
}
