#define M_PI 3.1415926535897932384626433832795
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // input coordinates within [-1 1]
	vec2 uv = 2.0*fragCoord.xy / iResolution.xy - vec2(1.0,1.0);

    // input time from 0 to 1
    float progress = fract(iTime*0.1);

    // intermediate variables
    float angleFrac = (atan(uv.y, uv.x)/(2.0*M_PI)+0.5);
    float norm = sqrt(dot(uv,uv));

    // simple progression
    //float activationTime = norm;

    // step progression
    //float activationTime = 0.1*floor(10.0*norm);

    // bad spiral
    //float activationTime = 0.1*(angleFrac+floor(10.0*norm));

    // good spiral
    //float activationTime = 0.1*(angleFrac+floor(10.0*norm-angleFrac));

    // removing sharp corner
    float x = 10.0*norm-angleFrac;
    float activationTime = 0.1*(angleFrac+floor(x)+fract(x)*0.2);

    if (activationTime <= progress)
	  fragColor = vec4(1.0,1.0,1.0,1.0);
    else
	  fragColor = vec4(angleFrac,0.0,0.0,1.0);
}
