mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),sin(angle),sin(angle));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 mouse = iMouse.xy / iResolution.xy;

	float bg = (cos(uv.x*3.14159*2.0) + sin((uv.y)*3.14159)) * 0.1;

	vec2 p = uv/2.8*cos(20.0)/iTime - bg/0.1;
    vec2 v = rotate2d(iTime) * uv/0.1;
    p *= vec2(v.x, v.y);
    vec2 p2 = uv*2.0 - 1.0;
	p *= 35.0;
    p2 *= 15.0;
	vec2 sfunc = vec2(p.x, p.y);
    vec2 sfunc2 = vec2(p2.x, p2.x);
	sfunc.y /= 0.1; // Thickness fix
    sfunc2.y /= 0.1;
    p *= vec2(v.x, v.y);

	vec3 c = vec3(abs(sfunc.y));
    vec3 c2 = vec3(abs(sfunc2.y));
	c = pow(c, vec3(-0.5));
    c2 = pow(c2, vec3(-0.5));
	c *= vec3(0.3,0.85,1.0);
    c2 *= vec3(0.3,0.85,1.0);

	fragColor = vec4(c+c2,1.0);
}
