bool IsColorAlmostColor(vec4 color, float minMargin, float maxMargin,float minSat)
{
    float maxValue,minValue;

    float hueVal;

    maxValue = max(max(color.x,color.y),color.z);
    minValue = min(min(color.x,color.y),color.z);

    float L = (maxValue + minValue)/2.;

    if(L<0.5)
    {
       if((maxValue - minValue)/(maxValue+minValue) < minSat)
           return false;
    }
    else
    {
        if((maxValue - minValue)/(2.0-maxValue - minValue) < minSat)
            return false;
    }

    if(maxValue == color.x)
    {
        hueVal =  (color.y-color.z)/(maxValue-minValue);
    }
    else if(maxValue == color.y)
    {
        hueVal = 2.0 + (color.z-color.x)/(maxValue-minValue);
    }
    else
    {
        hueVal = 4.0 + (color.x-color.y)/(maxValue-minValue);
    }

    hueVal*=60.;

    if(hueVal < 0.)
        hueVal +=360.;

    return hueVal >= minMargin && hueVal <= maxMargin;

}

vec4 GetColorForCoords( vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 fragColor = texture(iChannel0,uv);

    if(!IsColorAlmostColor(fragColor, 70.,150.,0.4))
    {
        float average = (fragColor.x + fragColor.y + fragColor.z)/3.;
    fragColor = vec4(average,average,average,average);
    }
    else
    {
        fragColor = texture(iChannel1,uv);
    }

    return fragColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
