vec4 DrawStone(vec2 uv,vec2 po,float r){
    if(distance(uv,po)<=r)return vec4(texture(iChannel3,uv).rgb*distance(uv,po)*8.,1.);
    else return vec4(0);
}

vec4 DrawCampfire(vec2 uv){
    vec4 CampfireTexture = vec4(0.);
    uv.x+=sin(iTime)*sin(uv.y*9.)/48.;
    if(uv.x>=0.35)if(uv.x<=0.65)
	CampfireTexture = texture(iChannel1,uv);
    uv.x-=sin(iTime)*sin(uv.y*9.)/48.;
    vec4 StoneTexture = DrawStone(uv,vec2(0.5 + 0.15,0.),0.1);
    if(StoneTexture.a>=0.5)
    CampfireTexture=StoneTexture;
    StoneTexture = DrawStone(uv,vec2(0.5 - 0.15,0.),0.1);
    if(StoneTexture.a>=0.5)
    CampfireTexture=StoneTexture;
    StoneTexture = DrawStone(uv,vec2(0.5,0.),0.15);
    if(StoneTexture.a>=0.5)
    CampfireTexture=StoneTexture;
    return CampfireTexture;
}

vec2 rotate(vec2 uv,float angle){
    return vec2(uv.x*cos(angle)-uv.y*sin(angle),uv.x*sin(angle)+uv.y*cos(angle));
}

vec4 SpaceShader(vec4 color,float bass){
    float bright=color.x*color.y*color.z;
    if(bright<bass)return vec4(0);
    return vec4(bright);
}

vec4 DrawBackground(vec2 uv){
    vec4 BackgroundTexture=vec4(0.);
    uv.x/=2.;uv.x+=0.25;
    if(distance(uv,vec2(0.5,0.))<=0.7) BackgroundTexture =  ((vec4(0.,0.5,0.,1.)+texture(iChannel3,uv*12.))/2.)*(1.-distance(uv,vec2(0.5,0.))*2. );
    if(BackgroundTexture.a<0.0001)BackgroundTexture=SpaceShader(texture(iChannel2,rotate(uv,iTime/64.)*4.),0.);
    return BackgroundTexture;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 CampfireTexture = DrawCampfire(uv);
    fragColor=DrawBackground(uv);
    if(CampfireTexture.a>0.1)fragColor=CampfireTexture;


}

vec4 renderFlame(vec2 uv){
    vec4 orange = vec4(255./256.,105./256.,0.,1.);
    vec2 sound = texture(iChannel0,vec2(uv.x)).rg;
    float level = (sound.x+sound.y)/2.;

    level+=texture(iChannel2,vec2(uv.x)).x/4.;
    level/=2.;
    level-=abs(0.5-uv.x);
    level*=3.;
    if(uv.y<level)
    return orange*(uv.y/level);
    else
    return vec4(0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float copies=12.;
	fragColor = (texture(iChannel1,uv)*copies+renderFlame(uv))/(copies+1.);
}
