////////////////////////////////////////////////////////////////////////////////																				
//	print stuff based on iq/P_Malin https://www.shadertoy.com/view/ldsyz4
const int[] font = int[]( 0x75557, 0x22222, 0x74717, 0x74747, 0x11574, 0x71747, 0x71757, 0x74444, 0x75757, 0x75747 );

void PrI( inout vec4 c, vec2 uv, int digitID, int value ) {
	int d				=	int( floor( pow( 10., float( digitID ) ) * 1.00001 ) );		//	* 1.00001 to prevent floor( 99.9999 )
    int n				=	(value/d) % 10;
    ivec2 p				=	ivec2(floor(uv * vec2(4.0, 5.0)));
    int i				=	p.x + p.y*4;
	c					=	vec4( vec3( (font[n]>>i) & 1 ), 1. );
}
void Pr( vec2 uv, vec2 xy, float s, int value, inout int digitID, inout bool b, inout int v, inout vec2 u ) {
	uv					=	( uv - xy ) / s;
    int maxDigits		=	1 + int( floor( log( float( value ) * 1.00001 ) / log( 10. ) ) );	//	* 1.00001 to prevent floor( 2.99999 ) -- but has leading 0 on 99999
    int digit			=	int(floor(-uv.x));
	if( abs( uv.y - 0.5 ) <= 0.5 && digit >= 0 && digit < maxDigits )
        digitID = digit, v = value, b = true, u = fract( uv );	//	save everything for PrI so inlined/executed only once
}
////////////////////////////////////////////////////////////////////////////////
void mainImage( out vec4 o, vec2 i )
{
    vec4 M = iMouse; float c, t, p = 6.28, T = iTime;
    vec2 U = i / iResolution.xy, s = vec2( 90., .02 ) + abs(M.zw) + .005 * (M.xy + vec2( sin( T ), cos( T ) ) );
	c				=	dot( sin( i ), 1. / sin( i ) );
	i				+=	c * s.x * sin( s.y * i.yx );
    i				=	sin( .025 * i );
    c				=	.5 * ( i.x + i.y );

    mat3 m			=	mat3(
		        			vec3( i, 1 ),
		        			vec3( -c, c, c - .1 ),
		        			vec3( -2.*c, 1.-6.*abs(c), 2.*c)  			
    );
	t				=	T * p / 15. + 1.5;	//	see link below
	t				=	( t + cos( t ) ) / p - .25;
    o				=	vec4( m * max( 1. - abs( vec3( 0, 1, 2 ) - mod( t, 3. ) ), 0. ), 1 );	//	see link below

    bool b = false; int digit = 0, value = 0; vec2 u = U;
    for ( int j = 0 ; j < 4 && !b; j++ ) Pr( U, vec2( .2 + .2 * float( j ), .1 ), .03, int( abs( M[j] ) ), digit, b, value, u );
    if ( b ) PrI( o, u, digit, value );
}
////////////////////////////////////////////////////////////////////////////////
//	https://www.wolframalpha.com/input/?i=y%3D(x*6.28%2B1.5%2Bcos(x*6.28%2B1.5))%2F6.28-.25
//	https://www.wolframalpha.com/input/?i=y%3Dmax(+1.+-+abs(+-+mod(+x,+3.+)+),+0.+);+y%3Dmax(+1.+-+abs(+1-+mod(+x,+3.+)+),+0.+);+y%3Dmax(+1.+-+abs(2+-+mod(+x,+3.+)+),+0.+)
