float rand(float x){return fract(sin(x)*1e4);} 
vec4 blend(vec4 c, vec4 o){return o*o.a + c*(1.-o.a);}

vec3 hsv(vec3 c) {
    vec4 k=vec4(1.,2./3.,1./3.,3.);
    vec3 p=abs(fract(c.xxx+k.xyz)*6.-k.www);
    return c.z*mix(k.xxx,clamp(p-k.xxx,0.,1.),c.y);
}

vec4 city(vec2 uv, float zoomx, float speed, float hCenter, float hAmpl, float hue, float wavespeed) {
    uv.x+=iTime*speed;
    uv.x*=zoomx;
    uv.x+=rand(floor(uv.x))*.5;
    uv.y+=sin(floor(uv.x)*.5+iTime*wavespeed)*.1;
    float h=mix(hCenter-hAmpl, hCenter+hAmpl, uv.y);
    float s=step(rand(floor(uv.x)),h);
    float y=mix(.2, 1., uv.y);
    vec3 c=hsv(vec3(hue, 1., 1.));
    return mix(vec4(c,y),vec4(0.),s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 c=vec4(hsv(vec3(.75,1.,mix(.6,.1,uv.y))),.0);
    for (int i = 0; i < 4; ++i) {
        float f=float(i);
    	c=blend(c,city(uv, 40.-f*8., .2+f*.02, .4+.05*f, .4+.2*f, .7-f*.05, 2.+f*.4));
    }
	fragColor = c;
}
