// by Nikos Papadopoulos, 4rknova / 2017
// WTFPL

void mainImage(out vec4 c, in vec2 p)
{
	p = 7.5 * (p.xy / iResolution.xy * 2. - 1.)
           * vec2(iResolution.x / iResolution.y,1);
    float r = sqrt(p.x * p.x + p.y * p.y)
    	, a = atan(p.y,p.x)
    	, f = r + .5 + 1.0 * sin(10. * a + 2. * r * r);
  	c = vec4(vec3(abs(1./(f*f))),1);
}
