#define PI 3.141592654
#define t iTime

float core(float r, float dist) {
	return pow(max(0., r - dist), .2);
}

float petal(float r, float phi, float dist) {
	return pow(max(0., r*cos(phi*8.) - dist), .3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy - vec2(.5, .5);
	uv.x *= iResolution.x/iResolution.y;

    float r11 = (.5 + .05 * sin(t + PI)),
          r22 = (.4 + .05 * sin(t*2.)),
            f = .6 * texture(iChannel0, .4 * uv + vec2(.5, .0)).r + .2,
            m = f,
            r = f / 2.,
           r1 = f,
           r2 = f,
         dist = length(uv),
          phi = -atan(uv.x, uv.y) + t / 5. + f;

	fragColor = vec4(1,1,0,0)*core(r, dist)
              + vec4(1,0,0,0)*petal(r1, phi, dist)
              - vec4(0,1,0,0)*petal(r1, phi, dist)
              + vec4(1,2,0,0)*petal(r2, phi + PI/8., dist)
              - vec4(0,1,0,0)*petal(r2, phi + PI/8., dist)
              + m * vec4(f, .3, .5, 0.);
}
