float noise(float n,float s,float res)
{
	float a = fract(sin(((floor((n)/s-0.5)*s)/res)*43632.6326)*53356.6426);
	float b = fract(sin(((floor((n)/s+0.5)*s)/res)*43632.6326)*53356.6426);
	return mix(a,b,smoothstep(0.0,1.0,+mod(n/s+0.5,1.0)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float height = (noise(fragCoord.x+iTime*16.0,512.0,iResolution.x)+
					noise(fragCoord.x+iTime*32.0,256.0,iResolution.x)+
				    noise(fragCoord.x+iTime*64.0,128.0,iResolution.x)+
				    noise(fragCoord.x+iTime*128.0,64.0,iResolution.x))/4.0;
	float shape = clamp(sign(height-fragCoord.y/iResolution.y),0.0,1.0);
	vec3 color = vec3(noise(fragCoord.x-iTime*64.0,512.0,iResolution.x),
					  noise(fragCoord.x+iTime*32.0,512.0,iResolution.x),
					  noise(fragCoord.x-iTime*128.0+50.0,256.0,iResolution.x))*shape;
	fragColor = vec4(color.r,color.g,color.b,1.0);
}
