vec3 warp(vec3 p){
	p=abs(p)/dot(p,p);
	return p;	
}

float dist(vec3 p){
    float time = sin(iTime*3.14159*0.1)*6.-1.;
	for(int i=0 ; i<30 ; i++)
		p=warp(p)-vec3(0.43,0.28,0.31)+time*0.005-0.01;
	return length(p*5.);	
}

float intersect(vec3 ro, vec3 rd){

	float d=1000000.,
		  t=0.001;

	for(int i=0 ; i<50 ; i++){
		float td=dist(ro+t*rd);
		d=min(d+t*1.,td);
		t+=0.001;
	}
	return d;
}

vec3 shade(vec2 uv){

	vec3 ro=vec3(-0.21,0.203,-0.25);
	vec3 rd=normalize(vec3(uv,-1.));

	float d = intersect(ro,rd);
	d=exp(-exp(-d*2.2)*40.);
	//d=sin(d*12.+1.)*0.5+0.5;

	return vec3(d)*vec3(3.,1.35,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
	fragColor = vec4(shade(uv),1.0);
}
