vec3 MAGENTA = vec3(1.,0.,1.);
float box(vec2 p, float strokeWeight) {
    vec2 weight = vec2(strokeWeight);
    vec2 walls = step(weight, p);
    return walls.x * walls.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	int columns = 15;
    int rows = 10;
    vec2 pixel = fragCoord.xy / iResolution.xy;
    vec2 grid = vec2(columns, rows);
    vec2 tile = fract(pixel * grid - vec2(0.5));
    float lines = box(tile, 0.1);
    vec3 color = MAGENTA * vec3(1. - lines);
	fragColor = vec4(color,1.0);
}
