void mainImage( out vec4 O, vec2 f )
{
    vec2 r = iResolution.xy,
         v = (f-.5*r) / min(r.x, r.y),
	     t = mod( f-.5, floor(v.x*.2+4.) );
    float g = t.x + (0.<v.y ? t.y : 0.),
          d = round(length(v)*1.5), 
          b = 10.;

    f = step(r-b-b, abs(f+f-r));
    O = vec4( g * d + f.x+f.y);
}

