void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;

   // anim
    vec2 c1 = 0.4*sin( iTime*1.0 + vec2(4.0,0.5) + 6.0);
    vec2 c2 = 0.2*sin( iTime*1.3 + vec2(2.0,1.0) + 2.0);
    vec2 c3 = 0.3*cos( iTime*1.5 + vec2(0.5,0.1) + 4.0);
    vec2 c4 = 0.1*sin( iTime*1.8 + vec2(2.0,2.0) + 8.0);
    vec2 c5 = 0.4*cos( iTime*2.3 + vec2(1.2,1.1) + 3.0);
    vec2 c6 = 0.2*sin( iTime*1.1 + vec2(0.9,0.3) + 1.0);


   // potential (4 metaballs)
    float v = 0.0;    
    v += 1.0-smoothstep(0.0,0.3,length(uv-c1));
    v += 1.0-smoothstep(0.0,0.4,length(uv-c2));
    v += 1.0-smoothstep(0.0,0.3,length(uv-c3));
    v += 1.0-smoothstep(0.0,0.29,length(uv-c4));
    v += 1.0-smoothstep(0.0,0.33,length(uv-c5));
    v += 1.0-smoothstep(0.0,0.21,length(uv-c6));

   // color    
    //vec3 col = mix( vec3(v), vec3(1.0,0.6,0.0), smoothstep(0.9,0.91,v) );
    vec3 col = mix( vec3(v), vec3(0.9,0.7,0.0), smoothstep(0.1,0.1,v) );

    fragColor = vec4(col,1.0);
}
