bool isInCircle(in vec2 v, in vec2 center, in float radius)
{
	return distance(v, center) < radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;

	float ratioWH = iResolution.x / iResolution.y;

	vec2 center1 = vec2(0.5*ratioWH + 0.2*cos(3.0*iTime), 0.5 + 0.2*sin(5.0*iTime));

	vec2 center2 = vec2(0.5*ratioWH - 0.2*cos(5.0*iTime), 0.5 - 0.2*sin(3.0*iTime));

	if(isInCircle(uv, center1, 0.05))
		fragColor += vec4(1,0,0,1);

	if(isInCircle(uv, center2, 0.05))
		fragColor += vec4(0,0,1,1);
}
