vec3 DARK_UI = vec3(.12);
vec3 BU_BLUE = vec3(.2,.4,.7);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 c = DARK_UI;
    float q = smoothstep(0.,1.,mod(iTime/15.,.1)/.1);
    float m = clamp(length(uv)*5.,0.,1.);
    if (abs(length(uv)-q/5.)<.005) {
    	c=mix(BU_BLUE,DARK_UI,m);    
    }
	fragColor = vec4(c,1.0);
}
