#define PI 3.1415927


float hash( vec2 p )
{
	float h = dot(p,vec2(127.1,311.7));

    return -1.0 + 2.0*fract(sin(h)*43758.5453123);
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

float makeWaves(in vec2 uv, in float scale, in float cycles, in float noiseOctave, in float noiseScale, in float ofs)
{
    vec2 v = vec2(uv.x, ofs);
    v.y += scale * sin(uv.x * cycles * PI);
    v.y += noiseScale * noise(noiseOctave * uv);
    float len = 1.0 - (uv.y - v.y);
    float c = smoothstep(0.996, 1.0, len);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy);

    vec4 bgColour = vec4(220.0 / 255.0, 232.0 / 255.0, 245.0 / 255.0, 1.0);
    vec4 finalColour = bgColour;

	float w0 = makeWaves(uv, 0.125, 3.0, 3.2, 0.25, 0.6);
    vec4 c0 = w0 * vec4(123.0 / 255.0, 191.0 / 255.0, 208.0 / 255.0, 1.0);
    finalColour = mix(finalColour, c0, vec4(w0, w0, w0, 1.0));

    float w1 = makeWaves(uv, 0.125, 3.0, 4.0, 0.25, 0.4);
    vec4 c1 = w1 * vec4(0.0, 86.0 / 255.0, 58.0 / 255.0, 1.0);
    finalColour = mix(finalColour, c1, vec4(w1, w1, w1, 1.0));

    float w2 = makeWaves(uv, 0.125, 1.0, 11.0, 0.07, 0.25);
    vec4 c2 = w2 * vec4(109.0 / 255.0, 172.0 / 255.0, 163.0 / 255.0, 1.0);
    finalColour = mix(finalColour, c2, vec4(w2, w2, w2, 1.0));

    float w3 = makeWaves(uv, sin((uv.x + 0.45) * PI * 2.5) * 0.22, 1.0, 19.0, 0.02, 0.1);
    vec4 c3 = w3 * vec4(16.0 / 255.0, 17.0 / 255.0, 45.0 / 255.0, 1.0);
    finalColour = mix(finalColour, c3, vec4(w3, w3, w3, 1.0));

    float w4 = makeWaves(uv, sin(uv.x * PI * 2.5) * 0.22, 1.0, 19.0, 0.02, 0.1);
    vec4 c4 = w4 * vec4(223.0 / 255.0, 169.0 / 255.0, 44.0 / 255.0, 1.0);
    finalColour = mix(finalColour, c4, vec4(w4, w4, w4, 1.0));

    float w5 = makeWaves(uv, 0.30 * uv.x, 1.2, 13.0, 0.02, 0.02);
    vec4 c5 = w5 * vec4(149.0 / 255.0, 67.0 / 255.0, 89.0 / 255.0, 1.0);
    finalColour = mix(finalColour, c5, vec4(w5, w5, w5, 1.0));

	fragColor = finalColour;
}
