/* 
   29.12.2015
   Yolka. Made by Madware
   Merry Christmas and Happy New Year!
*/

#define vc3 vec3
#define vc2 vec2
#define time iTime
#define px2 p.x * p.x
#define ss(x, y) sin((time + x) * y)
#define _f(x) * x + x
#define mx mix(col,
#define f1(x,y) f += x*ns(p); p *= y
#define area(v0, v1) dot(v0, vc2(v1.y, -v1.x))
#define IsIn(b) all(greaterThanEqual(b, vc3(0.))) && all(lessThanEqual(b, vc3(1.)))
#define smst smoothstep
#define snow(p)         smst(p.y, p.y + .005, sin(p.x + .2) * .2 - .25)
#define moon(p,pp,rad)  smst(.97, 1., distance(p, pp) / rad)
#define h(n) fract(sin(n)*753.5453)
#define flt float
#define fori(x) for(flt i = 0.; i < x; i++)
#define pa(x,y) pow(abs(x),y)
#define cmix col = mix
#define rres return f
#define clp clamp

float gir(vc2 p, vc2 pp, flt rad )
{    
    return smst(.95, 1., 1.- distance(p, pp) / rad);    
}

const flt PI = 3.141593;
const flt stp = PI * .4;
const flt deg45 = PI / 4.;

// noise from https://www.shadertoy.com/view/4sfGzS (simplified 2d version)
flt ns(vc2 x )
{
    vc2 p = floor(x);
    vc2 f = fract(x);
    f = f*f*(3. - f - f);

    flt n = p.x + p.y*157. + 113.;
    return mix(mix( h(n+ 0.), h(n+ 1.),f.x),
               mix( h(n+63.), h(n+64.),f.x),f.y);
}

flt fbm(vc2 p)
{
    flt f = 0.;
    f1(   .5,2.02);
    f1(  .25,2.03);
    f1( .125,2.01);
    f1(.0625,2.04);
    f /= .9375;
    rres;    
}

vc3 baryc(vc2 p0, vc2 p1, vc2 p2, vc2 p )
{
	vc2 e0 = p1 - p0;
	vc2 e1 = p2 - p1;

	vc2 v0 = p0 - p;
	vc2 v1 = p1 - p;
	vc2 v2 = p2 - p;

    flt a = area(e0, e1);
	flt a1 = area(v1, v2);
	flt a2 = area(v2, v0);
	flt a3 = area(v0, v1);

	return vc3(a1 / a, a2 / a, a3 / a);
}   

flt es( vc2 p0, vc2 p1, vc2 p2, vc3 _avec, vc2 p)
{
    flt f = 0.;
    vc2 _p = p + (p - p2) * (px2 * 1.25 + fbm(p*100.)*.055 + fbm(p*200.)*.035);
    _p.x -= sign(_p.x)*fbm(p*700.)*(px2 *.5);
    vc3 bar = baryc( p0, p1, p2, _p);
    if(IsIn(bar))    
	   f = dot(bar, _avec);    
    rres;    
}

flt star(vc2 sp, vc2 p, flt or, flt ir)
{
    flt f = 0.;
    flt ang = PI * .5 + ss(0., 1.5)*.05; 
    vc3 clr = vc3(1., .2, .2);

    fori(5.)
    {
        vc2 op = vc2(sp.x + cos(ang) * or, sp.y + sin(ang) * or);
        vc2 p1 = vc2(sp.x + cos(ang + deg45) * ir, sp.y + sin(ang + deg45) * ir);
        vc3 b = baryc(sp, op, p1, p);
        if(IsIn(b))
    	{
	   		f = dot(b, clr);
            break;
    	}

        vc2 p2 = vc2(sp.x + cos(ang - deg45) * ir, sp.y + sin(ang - deg45) * ir);

        b = baryc(sp, op, p2, p);
        if(IsIn(b))
    	{
	   		f = dot(b, clr);
            break;
    	}
        ang += stp;
    }    
    rres;    
}

void mainImage( out vec4 fragColor, in vc2 fragCoord )
{
    flt t =  ss( 0.,  1.);    
    flt t2 = ss( 0.,  3.) _f(.5);
    flt t3 = ss(-PI,  3.) _f(.5);
    flt t4 = t2 * .02;
    flt t5 = ss( 0., .75) * .03;

    flt a = 0.;
    flt ab = 0.;
	vc2 p = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    vc3 snc = vc3(.81, .73, .83);

    vc3 avec = vc3(.045, .36,  1.); 
    vc3 davec = vc3(.02,.01,0.);
    vc2 dp = vc2(.01, .05 - t4);    

	vc2 v1 = vc2(-.5,-.8); vc2 v2 = vc2(.5,-.8); vc2 v3 = vc2(0.,-t4);
	vc3 col = vc3(.1, .1, .11);
    vc3 wt = vc3(1.);
    vc3 girc = vc3(.1, .9, .17);

    if(t <= 0.)
        girc = mix(girc.yxz, girc, t + 1.);        
    else
        girc = mix(girc, girc.zxy, t);

    ab = es(v1, v2, v3, avec, p);
    if(ab > 0.) a = ab;

    fori(8.)
    {    
        v1 += vc2(.04, .15 - t4*.3); v2 += vc2(-.04, .15 - t4*.3); v3 += vec2(.0, .1 - t4*.2);    
        avec += davec;

        ab = es(v1, v2, v3, avec, p + dp);
        if(ab > 0.) a = mix(a, 0., .3);    
        ab = es(v1, v2, v3, avec, p);
        if(ab > 0.) a = ab;
        dp += vc2(0., -.0035);
    }       

    if(a > .1)
    {
        col = mix(vc3(0.), vc3(.18, .53, .26), a * (1. + fbm(p*150.)*.45));
        col += girc * t2 * .02;
        col += girc.zyx * t3 * .02;
        col = col + vc3(1., 0., 0.) * pow(clp(p.y - t4 + .1, 0., 1.), 2.) * 1.3 * t;

        flt kf = 1.;
        flt kk = 0.;

        for(flt k = -.75; k < .4; k += .15)
        {
            for(flt j = -.34; j < .58; j += .12)
            { 
                flt jj = j * kf;
                flt rg = gir(p,vc2(jj, k + kk + jj / 4. + pa(p.x, 2.)), .3) * t2;
                flt gg = gir(p,vc2(jj + .06 * kf, k + kk + (jj + .06 * kf) / 4. + pa(p.x, 2.)), .3) * t3;

                cmix(mx girc, rg), wt, pow(rg, 8.));
                cmix(mx girc.zyx, gg), wt, pow(gg, 8.));
                kf -= .011111;
            }   
            kk -= t4 * .25;
        }        
    }
    else
    {
        vc2 mlt = vc2(.8, .7 + t5);
        vc2 msd = vc2(.9, .7 - t5);

        flt l = length(mlt);
        flt an = atan(mlt.y, mlt.x) + mod(time *.1, PI);

        mlt = vc2(l * cos(an) * 2.1, l * sin(an) *.9 - .4);   
        msd += mlt - vc2(.8, .7 + t5);

        vc3 sky = vc3(.01+(-p.x * 1. - p.y) * .07, (p.x * 1. - p.y) * .1, .17+(p.x * 1. - p.y) * .07);

        flt snw = snow(p);

        col = mx sky, clp(p.y + .3, 0., 1.));

        if(snw > 0.) 
        {
            col = mx vc3(.81, .73, .83), snw * (1. - fbm(p * 150.) * .06 + p.y * .55));  
            if(es(vc2(-.5,-.8), vc2(.5,-.8), vc2(0.,-t4), avec, p + vec2(0., 0.05)) <= 0.01)
            {
                vc2 pp = vc2(p.x, p.y * 3. + 1.6);
            	flt aa = (1. - clp(distance(pp, vc2(0., -.5)) / 1.5, 0., 1.)) / 4.;

                col += girc * t2 * aa;
                col += girc.zyx * t3 * aa;
            }
        }
  		else   
        {
            fori(25.)
            {
                flt a1 = ss(i, .3) _f(.5);
                col = mx snc, a1 * gir(p, vc2(mod(h(i*72.04) * 6. - time * .02, 4.) - 2. ,h(mod(i,30.) * 200.) * 2. - 1.),.125 + a1*.05));                
            }
            col = mx vc3(.87, .8, .6), clp(moon(p,mlt, .24) - moon(p,msd, .2), 0., 1.));  
        }
    }

    v3 += vc2(.0,.035);

    a = star(v3, p, .13, .06);
    if(a > 0.)
    	cmix(vc3(.6 + .35 * t, .11, .14), vec3(1., .7, .7), star(v3, p, .115, .055) * (t _f(.5)) * .9); 

    fori(70.){
        flt a2 = ss(i + h(i), .3) _f(.5);
        col = mx snc*1.35, gir(p, vc2(h(i*72.04) *  4. - 2. + a2 ,mod((h(mod(i ,30.) * 200.) - time *.1) * 2., 2.) - 1.)*1.1,.35 + a2 *.25));
    }

	fragColor = vec4(col,1.);
}
