const vec4 grey1 = vec4(0.7,0.7,0.7,1.0);
const vec4 grey2 = vec4(0.6,0.6,0.6,1.0);
const vec4 white = vec4(0.9,0.9,0.9,1.0);
const float squareSize = 0.07;

const float crossThickness = 0.05;
const float crossWidth = 0.2;
const float speed = 2.;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uv =  (fragCoord.xy  / ( squareSize * iResolution.xx ) );          
    vec2 checkboardCoords =  floor(uv);

    // Checkboard
    if ((checkboardCoords.x+checkboardCoords.y != floor((checkboardCoords.x+checkboardCoords.y)*0.5)*2.))
    {
        fragColor = grey1;
    }
    else
    {
        fragColor = grey2;
    }        

    //floating part
     vec2 floatingPart = uv - checkboardCoords.xy;
    float xDiff = ( floatingPart.x > 0.5 ) ?  checkboardCoords.x + 1. - uv.x : uv.x - checkboardCoords.x;
    float yDiff = ( floatingPart.y > 0.5 ) ?  checkboardCoords.y + 1. - uv.y : uv.y - checkboardCoords.y;

    if ( xDiff < crossThickness && yDiff < crossWidth || yDiff < crossThickness && xDiff < crossWidth )
    {
               fragColor = mix(white,fragColor, 0.5 * ( sin(iTime* speed) + 1. ) );
    }





}
