void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t=iTime;
    float freq[1];
    freq[0] = texture( iChannel0, vec2(0.01+sin(mod(t,0.02)), 0.00 ) ).x;

    float shift=freq[0]/30.;

  //  float shift=0.01+sin(mod(t,0.02));

    float red 	=   texture(	iChannel0,vec2(uv.x+shift,uv.y)).r;
    float green =   texture(	iChannel0,vec2(uv.x-shift,uv.y)).g;
	float blue 	=   texture(	iChannel0,vec2(uv.x,uv.y)).b;

	vec3 color = vec3(red,green,blue);

	fragColor = vec4(color,1.0);
}
