#define M_PI 3.1415926535897932384626433832795

float borderPhi() {
	return iTime/2.0;
}

float border(in float x) {
 	return tan(borderPhi())*(x - iResolution.x/2.0) + iResolution.y/2.0;
}

float perpetDistance(in vec2 point) {
	return cos(borderPhi())*(border(point.x) - point.y);
}

vec2 perpetPoint(in vec2 point) {
    float d = perpetDistance(point);
    float phi = borderPhi();
    return vec2(point.x - 2.0*d*sin(phi), point.y + 2.0*d*cos(phi));
}

bool isPrimary(in vec2 point) {
    bool result = point.y <= border(point.x);
    if (mod(borderPhi() + M_PI/2.0, 2.0*M_PI) < M_PI) {
        return !result;
    }
    return result;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if (isPrimary(fragCoord)) {
    	fragColor = texture(iChannel0, fragCoord / iResolution.xy);
    } else {
     	fragColor = texture(iChannel0, perpetPoint(fragCoord) / iResolution.xy);
    }
}
