vec3 vmix(vec3 a, vec3 b, float t) {
	return vec3(mix(a.x,b.x,t),mix(a.y,b.y,t),mix(a.z,b.z,t));   
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float b = length(uv.y-.5);
    float aa = length((uv.x)-.5);
    float _a = min(aa,b);
    float _b = max(aa,b);
    _a = max(_a,-(uv.y-.5));
    float a = mix(_a,_b,(_a+_b)/2.);
    uv+=a*iDate.w;
    uv/=a;
	fragColor = pow(vec4(vmix(texture(iChannel0, uv).rgb,texture(iChannel1, uv).rgb,(_a+_b)/2.)*min((a+.25),1.),1.0), vec4(1./2.2));
}
