// see different implementation here: https://www.shadertoy.com/view/XsdyR7

// draw line
#define L(a,b) O+= 2e-2/length( clamp( dot(U-(a),v=b-(a))/dot(v,v), 0.,1.) *v - U+a ) // segments

void mainImage( out vec4 O, vec2 U )
{
    U -= .5; O-=O;
    vec2 M = vec2(1.31,1), v;

    U *= M *8./iResolution.y ;
    if ( mod(U.x,2.) > 1. )  U.y = .59-U.y; //, O.g+=.3; // odd columns

    U = 2.*fract(U)-1.; U.y -= .11; U /= M;              // local frame
    O.r += 1.-1.1*length(U);                             // red disk

    U.x = abs(U.x);                                      // horiz symmetry
    L(vec2(.5,-.8),vec2(.95,-2));                        // ext line to next tile

    float a = atan(U.x,U.y), l=length(U), e=.628;        // --- draw pentagon
    a = mod(a+e,e+e)-e;                                  // 5-fold symmetry
    U = l * vec2(cos(a),sin(a));
    L(vec2(.87,-1),vec2(.87,1));                         // draw 1 side




}
