vec3 colorA= vec3(0,0,0); // Black
vec3 colorB= vec3(1,0,0); // Red
vec3 colorC= vec3(1,1,1); // White

// Create Circle
float circle(vec2 uv, vec2 pos, float radius, float width)
{
    float dist = distance(pos, uv);
    float value = step(radius - width, dist) - step(radius, dist);

    return value;
}

// Create Rectangle
float rect(vec2 uv, vec2 shapePos, vec2 size)
{

    float value = step(shapePos.x,uv.x);
    value -= step(shapePos.x + size.x, uv.x);

   	value -= step(shapePos.y + size.y, uv.y);
    value -= 1.0 - step(shapePos.y, uv.y);
    return value;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;

    uv *= 3.;

    vec2 tileIdx = floor(uv);

    float offset = step(0., mod(uv.y, 1.));
    uv.x += offset*iTime;

    uv = fract(uv);

    uv.x *= ratio;

    //Top Red Cricle
    vec2 pos = vec2(0.5 * ratio, 0.5);
    float dist = distance(pos, uv) * .5;
    dist -= 0.15;

    float angle = atan(uv.y, uv.x);
    float radius = floor(sin(1. * angle));
    vec2 size = vec2(0.0,1.0);
    float value = step(radius, dist) - rect(uv,pos,size);
    vec3 color = mix(colorB, colorC, value);

    //Outer Black Circle
    pos = vec2(0.5 *ratio, 0.5);
    radius = abs(0.3);
    float width = 0.01;
    value = circle(uv, pos, radius, width);
    color = mix(color, colorA, value);

    //Middle White Circle
    radius = abs(0.10);
    width = 1.0;    
    value = circle(uv,pos, radius, width);
    color = mix(color, colorC, value);

    //Inner Black Circle   
    radius = abs(0.10);
    //dist += 0.20;
    width = 0.02;    
   	value = circle(uv, pos, radius, width);
    color = mix(color, colorA, value);

    //Inner Most Black Circle   
    radius = abs(0.05);
    width = 0.01;    
   	value = circle(uv, pos, radius, width);
    color = mix(color, colorA, value);

    //Right Rectangle
    pos = vec2(0.55 * ratio, 0.47);
    size = vec2(0.21,0.06);
    value = rect(uv, pos, size);
    color = mix(color, colorA, value);

    //Left Rectangle
    pos = vec2(0.335 * ratio, 0.47);
    size = vec2(0.2,0.06);
    value = rect(uv, pos, size);
    color = mix(color, colorA, value);

    fragColor = vec4(color, 1.0);

}
