#define pi 3.1415927
vec2 expo (vec2 v) {
	return exp(v.x)*vec2(sin(v.y),cos(v.y));
}
vec2 ln (vec2 v) {
	return vec2(log(length(v)), atan(v.x,v.y));
}
vec2 mul (vec2 a, vec2 b) {
	return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);
}
vec3 f (vec2 v) {
    //v = v/dot(v,v);//inverting space does not alter fractal!
    for (int i = 0; i < 19; i++) {
        v = ln(v);
        v = expo(mul(v,v));
    }
    return abs(clamp(10.*v.xyy,-.1,1.));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    vec2 uv = fragCoord.xy/iResolution.xy*2.-1.;
    uv.x *= iResolution.x/iResolution.y;
    float t = 0.1*iTime;
    mat2 m = mat2(sin(t),cos(t),-cos(t),sin(t));
    float scale = 20.*exp(-16.*(-cos(0.2*iTime)*0.5+0.5));
    vec2 v = scale*((m*uv))-vec2(0,0.04249);
    vec3 e = scale*vec3(1,1,0)/iResolution.xyx;

    fragColor = vec4((f(v)+f(v+e.xz)+f(v+e.zy))/3.,1.0);
}
