void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec4 sam = texture(iChannel0, uv);

    // Output to screen
    fragColor = vec4(sam.zyx * 500000.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;

    vec4 info = texture(iChannel0, uv);
    vec2 vel = info.xy;
    float p = info.z;

    vec2 pad = vec2(0.005);

    vec4 left = texture(iChannel0, uv + vec2(-pad.x, 0.0));
    vec4 right = texture(iChannel0, uv + vec2(pad.x, 0.0));
    vec4 top = texture(iChannel0, uv + vec2(0.0, pad.y));
    vec4 bottom = texture(iChannel0, uv + vec2(0.0, -pad.y));

    vec2 vel_p = left.xy - right.xy + top.xy - bottom.xy;
    p = (vel_p.x + vel_p.y) * 0.25;


    vel.x = (left.z - right.z) * 0.25;
    vel.y = (top.z - bottom.z) * 0.25;


    float dist_to_mouse = length(uv - mouse);
    if(dist_to_mouse < 0.05 && iMouse.z > 0.0){
      //vel += (uv - mouse);
      vel += vec2(10.0,0.00);
    }

    vel *= 0.99;
    p *= 0.99;

    fragColor = vec4(vel, p, 0.0);
    //fragColor = vec4(0.0);
}
