    vec4 p, d = vec4(.8,0,U/iResolution.y-.8);
    for( float f, t = 2e2 ; --t > 0. ; p = .05*t*d )
        p.xz += iTime,
        f = .5, f = T+T+T+T -p.w-.5 ,
        f > 0. ? O += ( (1.-O)*vec4(1,.8,.6,0) - f ) *f *.4: O; }
