void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelPos = fragCoord.xy/iResolution.xy;// Koordinaten des aktuellen Pixels 
    vec4 colChan0 = texture(iChannel0, pixelPos);//Pixel des Channel0 erhalten an Positon xy
    vec4 colChan1 = texture(iChannel1, pixelPos);//Pixel des Channel1 erhalten an Positon xy

    /*
		Wenn der rote und blaue Anteil zusammen kleiner als der grüne Anteil sind, befindet sich an 
		dieser Stelle ein Greenscreen und kann entfernt werden.

		.r = Roter Anteil
		.g = Grüner Anteil
		.b = Blauer Anteil
	*/ 
    if((colChan0.r + colChan0.b) < colChan0.g  ){

        colChan0 = colChan1;// Pixel von Channel 0 durch Hintergrund (Channel 1) ersetzen
    } 

    // Bearbeitetes Pixel des Channel 0 ausgeben über fragColor
    fragColor = colChan0; 

}
