#define PI 3.141592653589793
#define trans_frequency 10.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(.5, 1.);
	//uv.x *= iResolution.x / iResolution.y;
    vec2 up = vec2(0, 1);
    float c = ceil(iTime / 2. / PI);

    // monotonically decreasing
    float time = sin(iTime + PI / 2.0) + 1.;
    float rad = pow(acos(dot(up, normalize(uv))) / PI, 60. * time);
    vec3 col = vec3(rad) * (0.5 + 0.5*cos(c + vec3(0,2,4)));
    //fragColor = vec4(rad, rad, rad, 1.);
    fragColor = vec4(col, 1.);

}
