void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0,uv).yyy*10.;
    fragColor = vec4(sin(2.*col*vec3(1,2,3)+0.1*iTime)*0.5+0.5,1.0);



}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 x = texture(iChannel0,fract((fragCoord.xy+vec2(0,0))/iResolution.xy));
		#define df vec4(.3,0.5,0.4,0.1)
    	if (x.z < 0. || x.x < 0.4) x.x = 0.;
		x.x = x.x+x.x*(-x.y+x.w);
		x.y = x.y*0.93 + 0.003*x.x;
		x.w = x.w*0.9 + 0.03*x.x*x.z;
		x.z = x.z+0.02*(1.-x.z)-0.017*x.x;
		bool b = mod(fragCoord.y,2.) < 1.;
    	x = x*(1.-df) + df/6.*(
            texture(iChannel0,fract((fragCoord.xy+vec2(1,0))/iResolution.xy))+
            texture(iChannel0,fract((fragCoord.xy+vec2(0,1))/iResolution.xy))+
            texture(iChannel0,fract((fragCoord.xy+vec2(-1,0))/iResolution.xy))+
            texture(iChannel0,fract((fragCoord.xy+vec2(0,-1))/iResolution.xy))+
            texture(iChannel0,fract((fragCoord.xy+vec2(b?-1:1,1))/iResolution.xy))+
            texture(iChannel0,fract((fragCoord.xy+vec2(b?-1:1,-1))/iResolution.xy))
        );
    vec2 vv = fragCoord.xy/iResolution.xy;
		fragColor = x;
		if (iFrame < 3) fragColor = vec4(sin(12.*vv.x)*cos(12.*vv.y)>0.9?1:0,0,1,0);

}

