//This function convert color from hsv to rgb
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    int numSteps = 256;
    float scale = 2.0;
    vec2 translate = vec2(-0.7485,0.1);
    vec2 z, c;

    scale = 0.0001;
    scale += abs(pow(sin(iTime*0.2),8.0)*2.0);

    c.x = 1.3333 * (uv.x - 0.5) * scale + translate.x;
    c.y = (uv.y - 0.5) * scale + translate.y;

    int i;
    z = c;

    for(i=0; i<numSteps; i++) {
        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (z.x * z.y + z.y * z.x) + c.y;

        if((x * x + y * y) > 4.0) break;
        z.x = x;
        z.y = y;
    }

    float r = (i == numSteps ? 0.0 : float(i)) / 100.0;
    vec3 q = hsv2rgb(vec3(r+fract(iTime*0.3),2,2));

    // Output to screen
    fragColor = vec4(1.0-q,1);
}
