// -----------------------------------------------------------------------
// BPT.2016 - cute hills for in the backround of a platformer
float cartoon_hills( float x, float m, float k, float n, float p, float j, float s ) {
	return pow( pow( pow( 1.0 + sin(m * x), j ) * 0.5 - 1.0 + sin( p * x) * 0.5,j) * n, k ) * s;
}
// -----------------------------------------------------------------------

float f( float x ) {

	//float a = cartoon_hills( x, 0.0, 0.1, 0.38, 1.0, 2.0, 5.0 ); // rounded
	float a = cartoon_hills( x * 0.1, 2.9, 0.1, 10.5, 1.2, 2.0,  5.0 ); // reasonable
	float b = cartoon_hills( x * 0.25, 1.7, 0.2, 0.56, 2.3, 2.0,  5.0 ); // reasonable
	return mix( a, b, step(sin(iTime * 1.5 ),0.0) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord.xy / iResolution.x - vec2(0.5,0.0)) * 45.0;
	fragColor = vec4( vec3( (f(p.x+iTime) - p.y)), 1.0 ) * vec4( 0.20, 0.5, 0.0, 1.0 );
}
