#define PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    uv = uv*2.0-1.0;
    uv.x *= iResolution.x / iResolution.y;
    uv.y += sin(uv.y*1.5+iTime);
    //uv.y += sin(uv.y*10.0+iTime);
    uv.y += sin(uv.x)*cos(uv.x);
    uv.y += sin((uv.y*10.0+iTime)/2.0);
    uv.x += sin((uv.x+uv.y+iTime)/2.0);
    uv.x += sin(sqrt(uv.x*uv.x+uv.y*uv.y+1.0)+iTime);

    vec3 shiet = vec3(.5+sin(PI*uv.y),cos(PI*uv.x),abs(sin(iTime)));
    //vec3 shiet = vec3(sin(PI*uv.y),cos(PI*uv.x),cos(uv.y));
    //vec3 shiet = vec3(sin(PI*uv.x),sin(PI*uv.x+2.0*PI/3.0),sin(PI*uv.x+4.0*PI/3.0));
    //vec3 shiet = vec3(.3*abs(sin(PI*uv.y)),0.9*abs(sin(PI*uv.x)),1.5-abs(cos(PI*uv.y)));
   	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    fragColor = vec4(shiet,1.0);
}
