#define smoothify(edge,invalue,smoothrange) smoothstep(edge - smoothrange, edge + smoothrange, invalue)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // Normalized pixel coordinates (from 0 to 1)
   vec2 xy = fragCoord/iResolution.xy;
   vec3 col;
   float smoothrange = 0.09;

   float stepfxn_y = smoothify(
       sin(xy.y * 90.0),
       sin(iTime + xy.y + xy.x) * 0.9,
       smoothrange
   );
   float stepfxn_x = smoothify(
       cos(xy.x * 160.0),
       sin(-iTime + xy.x * 2.0 - xy.y) * 0.9,
       smoothrange
   );
   col = vec3(abs(stepfxn_y - stepfxn_x));    


   // Output to screen
   fragColor = vec4(col,1.0); 	  // full color
   // fragColor = vec4(vec3(col.r),1.0); // for peepin channels
}
