void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec4 val = texture(iChannel0, uv);

    fragColor = vec4(val.r+val.g);
}

vec4 sand = vec4(1.0,0.0,0.0,0.0); // color sand
vec4 nothing = vec4(0.0,0.0,0.0,0.0); // color empty


float random()
{
	return fract(sin(dot(gl_FragCoord.xy*iTime, vec2(12.9898,78.233))) * 43758.5453);  
}

vec4 get_pixel(float x_offset, float y_offset)
{
	return texture(iChannel0, (gl_FragCoord.xy / iResolution.xy) + (vec2(x_offset, y_offset) / iResolution.xy));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 currentPixel = get_pixel(0.0, 0.0);
    vec4 dp = get_pixel(0.0,-1.0);
    vec4 up = get_pixel(0.0,1.0);

    if(floor(gl_FragCoord.y)!=0.0){
        if(currentPixel!=nothing){
            if(dp.g==1.0){
                fragColor.g = 1.0;
            }
        }
        if(fragColor.g==0.0&&fragColor.b==0.0){
            fragColor = up;
        }
        if(floor(fragCoord.y)>=(iResolution.y-1.0)){
            fragColor = nothing;
        }
    } else {
        fragColor = sand;
        fragColor.g = 1.0;
    }

    if (iMouse.z > 0.0){
        if(0.5<0.9*smoothstep(1.0,0.0,length(fragCoord.xy-iMouse.xy)*0.026)){
            if(currentPixel.r!=sand.r){
        		fragColor.r = sand.r;
            }
        }
    }
    if(iTime<=11.0){
        if(0.8<0.9*smoothstep(1.0,0.0,length(fragCoord.xy-iResolution.xy*0.5+vec2(sin(iTime*2.0)*100.0,0.0))*0.026)){
        	fragColor.r = sand.r;
        }
    }
}
