vec3 hsv(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 circle(float radius, in float lengthA)
{
    float distance = min(1., .01/abs(radius-lengthA));
	return distance*hsv(vec3(radius, 1, 1.-radius));
}


vec3 specialCenter(float radius, in float time)
{
    float distance = min(1., time*(.01/radius));
	return distance*vec3(1.-radius, 0., 0.);
}

float bpm = 100.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
    float r = length(uv);
    vec3 color = vec3(0, 0, 0);
    float time = fract(iTime*(bpm/60.));
    for(int i = 0 ; i < 12 ; i++)
    {
    	   color += circle(r, (float(i)+time)/10.);
    }
    color += specialCenter(r, time);
    color.x = (color.x+1.-time)/2.;
    fragColor = vec4(color, 1);
}
