void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  fragColor = abs(
    sin(
      reflect(
        normalize(
          vec4(
            1.6,
            0.0,
            1.5,
            1.0
          )
        ),
        vec4(
          iTime,
          0.2,
          0.5,
          (fragCoord.x / iResolution.x)
        )
      )
    )
  );
}
