https://www.shadertoy.com/view/Xsl3WH
*/
#define pie 3.1415926

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    uv.x = atan(uv.x, uv.y) / pie;
    uv.y = 1. / length(uv) * .2;

    vec3 col = vec3(.0);
    col.r = abs(50. / (150. * uv.x + sin(uv.y * 10. + iTime) * 90.));
    col.g = abs(50. / (150. * uv.y + sin(uv.x * 10.1 + iTime) * 90.));
    col.b = abs(50. / (150. * uv.y + sin(uv.y * 10.2 + iTime) * 90.));

    fragColor = vec4(col, 1.);
}
