void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = texture(iChannel0, uv).rgb;
    float grey = dot(col, vec3(1,1,1)) / 3.0;    
    vec3 img = texture(iChannel1, uv).rgb;

    vec2 uv0 = (uv*2.0 - 1.0) * 0.5;
    float length0 = sqrt(dot(uv0, uv0));
    float time = fract(iTime / 2.0) * 25.0;
    float mask = 1.0 - clamp(length0 * 20.0 - (time*(grey*0.5+0.5)),0.0,1.0);

    // Output to screen
    fragColor.rgb = (1.0-mask) * img + mask*vec3(1.0,1.0,1.0);
    fragColor.a = 1.0;
}
