float getLine( float inner, float outer, float value ){
	float color = smoothstep( inner, outer, value );
	color = color * 2. - 1.;
	color = 1.-abs(color);	
	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position = ( fragCoord.xy / iResolution.xy );
	vec2 newpos = position;
	newpos.x += sin((position.y*13.+iTime*7.)/3.)*.3;
	newpos.x += sin((position.y*4.+iTime*.3)/2.8)*.8;
	newpos.y += sin((position.x*14.+iTime*6.)/3.1)*.31;
	newpos.y += sin((position.x*3.+iTime*.2)/2.3)*.7;

	float color = getLine( .2, .5, newpos.x );
	float color2 = getLine( .2, .7, newpos.y );
	float color3 = getLine( .2, .21, newpos.x - newpos.y );
	float color4 = getLine( .2, .23, newpos.x * newpos.y );
	float color5 = getLine( .2, .25, newpos.x / newpos.y );

	vec3 outcolor = vec3(color + color2 + color3 + color4 + color5);
	outcolor.x *= position.x;
	outcolor.y *= position.y;
	fragColor = vec4( outcolor, 1. );
}
