void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Find distance from center for this one pixel
    float d = length(uv - vec2(0.5, 0.5));

    // Calculate clarity for the reigon we're operating on.
    float clarity = 10.0;
    // Be clearer by the center.
    clarity /= min(d, 1.0);

    // Lose information so a region relative to "clarity" is all the same pixel
    uv *= clarity;
    uv = floor(uv);
    uv /= clarity;

    // Uh... seemed like fun?
    uv = (1.0 + sin(uv + iTime)) * 0.5;

    fragColor = texture(iChannel0, uv);
}
