#define time  iTime
#define R iResolution.xy
void mainImage( out vec4 fragC, in vec2 fC)
{

//   vec2 pos = (fragCoord.xy/R.xy) * 8. - 4.; 
   vec2 pos = (fC.xy/R.xy) * 10. - 5.; 
    pos.x *= R.x / R.y;
    float s = .25, f = .0, k = f;
    vec3 p = vec3(pos, sin(time * .4) * .5 - .5)* s;    

    for( int i=0; i< 10; i++ )
    {
           p = abs(p)/dot(p,p)- 1.65;
           k = length(p) ;
           p = p*k+k;
   }

   f = dot(p,p)* s;
   fragC= vec4(f*.5, f *1.2, f * 5., 1.);;
}
