void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.*fragCoord.xy / iResolution.xy - 1.;
    mat2 rotMat;
    float th = iTime/0.5;
    float ti = th * 1.1;
    float z = sin(th) * 1.2 + 2.5;
    float zb = sin(ti) * 1.2 + 2.5;
    rotMat[0] = vec2(z*cos(th),z*sin(th));
    rotMat[1] = vec2(-z*sin(th),z*cos(th));
    mat2 oldRotMat;
    oldRotMat[0] = vec2(zb*cos(ti),zb*sin(ti));
    oldRotMat[1] = vec2(-zb*sin(ti),zb*cos(ti));
    uv.y *= -1.;
    vec4 tex = vec4(texture(iChannel0, uv * rotMat));
	vec4 tey = vec4(texture(iChannel0, uv * oldRotMat));
	fragColor = vec4((tey.xyz) * (tex.xyz),1.);
    fragColor.xyz *= 2.3*(1.-length(uv)/2.0);
}
