void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 pos = (uv - 0.5) * 2.0;

    float t = iTime / 1.0;
    float t1 = 0.99 + sin(t * 1.8) / 150.0;
    float t2 = 0.2 + sin(t / 1.3) / 20.0;
    float t3 = 0.55 + sin(t / 2.3) / 30.0;

    for(int i = 0; i < 50; i++) {
        pos = vec2(pos.x * pos.x - pos.y * pos.y, 2.0 * pos.x * pos.y) + vec2(t1 * t2, t1 * t3);
    }

    vec3 color = vec3(0.0, 0.0, 0.0);
    float d = distance(pos, vec2(0.0, 0.0));

    if(d < 1.0) {
        color += 1.0;
    }

	fragColor = vec4(color, 1.0);
}
