//Ethan Alexander Shulman 2018
mat2 rot2d(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c,s,-s,c);
}
float twave(float t) {
    t = fract(t);
   	return t*2.-max(0.,t*4.-2.); 
}
void mainImage(out vec4 o, in vec2 u)
{
	vec2 uv = u/iResolution.y*(.5+twave(iTime*.04)*1.5);
    for (float s = 1.; s < 6.; s+=s) uv = 1.-abs(vec2(uv.x,uv.y)*rot2d(uv.x*s+iTime*.2)/(1.-abs(uv.y)));
    o = cos(texture(iChannel0,uv*.01)*6.28+cos((iTime)*.07*vec4(.3,.9,4,4))*4.)*0.5+0.5;
}
