//Ethan Alexander Shulman 2018

float hash(vec2 p) {
    return fract(dot(p+vec2(.36834,.723), normalize(fract(p.yx*73.91374)+1e-4))*7.38734);
}

mat2 rot2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

void mainImage( out vec4 o, in vec2 u )
{
    o = texture(iChannel0,u/iResolution.xy);
    //return;
    //camera ray origin and direction
	vec3 ro = vec3(iTime*2.,cos(iTime*.1)*2.,-5.), rd = normalize(vec3((u*2.-iResolution.xy)/iResolution.x,1.));

    //ray march
    vec4 s = vec4(0.);//color sum
    for (float j = 1.; j < 32.; j++) {
        //current ray position
        vec3 p = abs(ro+rd*pow(j+hash(u+j*.1)*.3,1.3)*.3);

        //scene function
        float v = texture(iChannel0,p.xz*.01).x;
        p.y += cos(p.x*.2+(iTime*.4+p.z)*v*.1)*2.;
        vec3 lr = mod(p,1.)-.5;
        float dp = max(.1,min(2.,(abs(lr.z)+abs(lr.x))*abs(cos(p.y)*.7)*1.05)),
        d = length(lr)-dp;
        s += max(0.,abs(p.y)-1.)*max(0.,1.-abs(d)*10.)*vec4(sin(p.z*.4+.6),cos(p.z*.4+iTime*.1),-sin(p.z*.5),1)*v*.2;
        if (s.w >= 1.) break;
    }

    //blend background and post fx
    o = mix(vec4(.9),s,s.w);
}

void mainImage(out vec4 o, in vec2 u)
{
    //spectogram
    if (u.y > 1.) o = texture(iChannel1,(u-vec2(0,1))/iResolution.xy);
    else o = pow(texture(iChannel0,u/iResolution.xy),vec4(2))/(1.-u.x/iResolution.x*.5);
}
